const fetch = require('node-fetch');

async function fetchRecipe() {
  // Try to get API key from environment variable first, fallback to placeholder
  const apiKey = process.env.SPOONACULAR_API_KEY || 'YOUR_SPOONACULAR_API_KEY_HERE';

  if (!apiKey || apiKey === 'YOUR_SPOONACULAR_API_KEY_HERE') {
    throw new Error('SPOONACULAR_API_KEY environment variable not set. Please configure your API key.');
  }

  const url = `https://api.spoonacular.com/recipes/findByIngredients?apiKey=${apiKey}&number=20&ingredients=${$ingredients}`;

  try {
    const response = await fetch(url);
    const data = await response.json();

    // Pick the first recipe (or random one)
    const recipe = data[0]; // or data[Math.floor(Math.random() * data.length)]

    console.log('Recipe:', recipe);

    return JSON.stringify(data);
  } catch (error) {
    return 'Error fetching recipe: ' + error;
  }
}

return fetchRecipe();
